<div class="card card-dark shadow">
    <div class="card-header">
        <h3 class="card-title">Tambah Vendor</h3>
        <div class="card-tools">
            <button type="button" class="btn btn-tool" data-card-widget="remove"><i class="fas fa-times"></i>
            </button>
        </div>
        <div class="card-tools">
            <button type="button" class="btn btn-tool" data-card-widget="collapse"><i class="fas fa-minus"></i>
            </button>
        </div>
    </div>
    <div class="card-body" style="display: block;">
        <form>
            <div class="row">
                <div class="col-lg-6">
                    <div class="card-body" style="display: block;">
                        <div class="form-group">
                            <label for="VendorName">Nama Vendor</label>
                            <input type="text" class="form-control" id="VendorName" placeholder="Masukkan Nama"
                                min="1" max="12">
                        </div>
                    </div>
                </div>
                <div class="col-lg-6">
                    <div class="card-body" style="display: block;">
                        <div class="form-group">
                            <label for="VendorNumber">Nomor Whatsapp</label>
                            <input type="text" class="form-control" id="VendorNumber" placeholder="Masukkan Nomor"
                                min="1" max="12">
                        </div>
                    </div>
                </div>
                <div class="col-lg-12">
                    <div class="card-body" style="display: block;">
                        <div class="form-group">
                            <label for="VendorAddress">Alamat</label>
                            <input type="text" class="form-control" id="VendorAddress" placeholder="Masukkan Alamat"
                                min="1" max="12">
                        </div>
                    </div>
                </div>
            </div>

        </form>
        <div class="card-footer bg-dark" style="display: block;">
            <button type="submit" class="btn btn-light float-right">Submit</button>
        </div>
    </div>
</div>
